// server/auth.ts
import passport from "passport";
import { Strategy as LocalStrategy } from "passport-local";
import session from "express-session";
import { scrypt, randomBytes, timingSafeEqual } from "crypto";
import { promisify } from "util";
var scryptAsync = promisify(scrypt);
async function hashPassword(password) {
  const salt = randomBytes(16).toString("hex");
  const buf = await scryptAsync(password, salt, 64);
  return `${buf.toString("hex")}.${salt}`;
}
async function comparePasswords(supplied, stored) {
  const [hashed, salt] = stored.split(".");
  const hashedBuf = Buffer.from(hashed, "hex");
  const suppliedBuf = await scryptAsync(supplied, salt, 64);
  return timingSafeEqual(hashedBuf, suppliedBuf);
}
function setupAuth(app2) {
  const sessionSettings = {
    secret: "mini-kepce-secret-key-2024",
    resave: true,
    saveUninitialized: true,
    cookie: {
      secure: false,
      maxAge: 1e3 * 60 * 60 * 24
    }
  };
  app2.set("trust proxy", 1);
  app2.use(session(sessionSettings));
  app2.use(passport.initialize());
  app2.use(passport.session());
  let users2 = {};
  let nextUserId = 1;
  passport.use(
    new LocalStrategy(async (username, password, done) => {
      try {
        const user = users2[username];
        if (!user || !await comparePasswords(password, user.password)) {
          return done(null, false, { message: "Invalid username or password" });
        }
        return done(null, user);
      } catch (error) {
        return done(error);
      }
    })
  );
  passport.serializeUser((user, done) => done(null, user.id));
  passport.deserializeUser((id, done) => {
    for (const username in users2) {
      if (users2[username].id === id) return done(null, users2[username]);
    }
    done(null, null);
  });
  app2.post("/api/register", async (req, res, next) => {
    try {
      if (users2[req.body.username]) {
        return res.status(400).send("Username already exists");
      }
      const newUser = {
        id: nextUserId++,
        username: req.body.username,
        password: await hashPassword(req.body.password),
        isAdmin: false
      };
      users2[req.body.username] = newUser;
      req.login(newUser, (err) => {
        if (err) return next(err);
        res.status(201).json(newUser);
      });
    } catch (error) {
      next(error);
    }
  });
  app2.post("/api/login", passport.authenticate("local"), (req, res) => {
    res.status(200).json(req.user);
  });
  app2.post("/api/logout", (req, res, next) => {
    req.logout((err) => {
      if (err) return next(err);
      res.sendStatus(200);
    });
  });
  app2.get("/api/user", (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);
    res.json(req.user);
  });
}

// server/storage.ts
import session2 from "express-session";
import MemoryStore from "memorystore";
var MemoryStorage = class {
  sessionStore;
  constructor() {
    this.sessionStore = new (MemoryStore(session2))({
      checkPeriod: 864e5
      // prune expired entries every 24h
    });
  }
};
var storage = new MemoryStorage();

// shared/schema.ts
import { pgTable, text, serial, integer, boolean, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
var users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  isAdmin: boolean("is_admin").notNull().default(false),
  createdAt: timestamp("created_at").notNull().defaultNow()
});
var galleryImages = pgTable("gallery_images", {
  id: serial("id").primaryKey(),
  title: text("title").notNull(),
  description: text("description"),
  imageUrl: text("image_url").notNull(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  createdBy: integer("created_by").references(() => users.id)
});
var insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
  isAdmin: true
});
var insertGalleryImageSchema = createInsertSchema(galleryImages).pick({
  title: true,
  description: true,
  imageUrl: true,
  createdBy: true
});

// server/routes.ts
function requireAdmin(req, res, next) {
  if (!req.isAuthenticated() || !req.user?.isAdmin) {
    return res.status(403).send("Admin access required");
  }
  next();
}
async function registerRoutes(app2) {
  setupAuth(app2);
  app2.get("/api/admin/gallery", requireAdmin, async (req, res) => {
    const images = await storage.getGalleryImages();
    res.json(images);
  });
  app2.post("/api/admin/gallery", requireAdmin, async (req, res) => {
    const parseResult = insertGalleryImageSchema.safeParse(req.body);
    if (!parseResult.success) {
      return res.status(400).json(parseResult.error);
    }
    const image = await storage.createGalleryImage({
      ...parseResult.data,
      createdBy: req.user?.id
    });
    res.status(201).json(image);
  });
  app2.delete("/api/admin/gallery/:id", requireAdmin, async (req, res) => {
    const id = parseInt(req.params.id);
    if (isNaN(id)) {
      return res.status(400).send("Invalid ID");
    }
    await storage.deleteGalleryImage(id);
    res.sendStatus(200);
  });
  app2.get("/api/gallery", async (req, res) => {
    const images = await storage.getGalleryImages();
    res.json(images);
  });
  const httpServer = createServer(app2);
  return httpServer;
}

